mapper.insert:
replace into archive_goal_score(stu_id,score,stu_name,course_id,total) values
(#{item.stuId},#{item.score},#{item.stuName},#{item.courseId},#{item.total})
select id,stu_id,stu_name,total from archive_goal_score where stu_id in
#{item.stuId}
and course_id=#{courseId}

mapper.select:
select score,total,stu_id,stu_name
from archive_goal_score
where course_id = #{courseId}
