mapper.select:
select t.id, t.user_id as userId, t.exam_id as examId, t.name, t.join_time as joinTime, t.quit_time as quitTime, (select score from exam where id = #{query.examId}) as totalScore, t.status, t.score, t.is_correcting as isCorrecting from (select a.id, a.user_id, a.exam_id, (select real_name from sys_user b where b.id = a.user_id) as name, a.join_time, a.quit_time, a.status, a.score, a.is_correcting from exam_attend_log a where exam_id = #{query.examId}) t where 1=1
and t.name like concat('%',#{query.name},'%')
and t.status = #{query.status}
and t.is_correcting=#{query.isCorrecting}
order by status , score desc

mapper.update:
update exam_attend_log set status=#{status}

                           ,quit_time=#{quitTime}

                       where exam_id = #{examId} and user_id = #{userId}

mapper.select:
select t.id, t.user_id as userId, t.exam_id as examId, t.name, t.join_time as joinTime, t.quit_time as quitTime, (select score from exam where id = #{examId}) as totalScore, t.status, t.score, t.is_correcting as isCorrecting from (select a.id, a.user_id, a.exam_id, (select real_name from sys_user b where b.id = a.user_id) as name, a.join_time, a.quit_time, a.status, a.score, a.is_correcting from exam_attend_log a where exam_id = #{examId}) t where 1=1
and t.status = #{status}
and t.is_correcting=#{isCorrecting}
order by status , score desc
