mapper.select:
select a.id,type,name,description,source,tips,advice_time,img,difficulty,kp_code,submit_times,correct_times,
       is_typical,option_num,used_num,a.create_time,a.update_time,updater,creator,deleted,status,version,b.problem_id,b.problem_option,b.is_true from problem_choice a
left JOIN(select problem_id,problem_option,is_true from problem_choice_options) b on a.id=b.problem_id where a.id =#{problemId}

mapper.update:
update lesson_problem
set wechat_status =1
where id=#{problemId}
