mapper.select:
select id,pid,name,description,code,sort,level from problem_knowledge_point
where pid=#{pid}

    and code=#{code}

order by code desc limit 1

select id,name,difficulty, problemType from (
select id,name,difficulty,1 as problemType from problem_choice where kp_code like concat(#{query.code},'%')
and difficulty=#{query.difficulty}
union all select id,name,difficulty,2 as problemType from problem_fill where kp_code like concat(#{query.code},'%')
and difficulty=#{query.difficulty}
union all select id,name,difficulty,3 as problemType from problem_code where kp_code like concat(#{query.code},'%')
and difficulty=#{query.difficulty}
)a where
not
exists(select 1 from judge_record b where a.id=b.problem_id and a.problemType=b.problem_type and #{query.userId}=b.user_id and submit_status=3)
