mapper.select:
select t1.id,t1.wx_name,t1.name,t1.phone,t1.union_id,t1.open_id,t1.area,t1.address,t1.age,t1.grade,t1.purpose,t1.status,t1.create_time, t1.update_time,t1.login_time,t1.start_number,t1.end_number,t1.remark,t1.integral,t1.balance,t1.deleted,t1.creator,t1.updater,t1.version, t2.teacher_opinion, t2.parent_opinion from enroll_user t1 left join enroll_evaluate t2 on t1.id = t2.student_id where t1.deleted = 0
and t1.name like concat('%',#{query.name},'%')
and t1.grade = #{query.grade}
and t1.status = 2 or status = 3
and t1.status = 1
and t1.status = 4
and t1.area =#{query.area}
and t1.purpose like concat('%',#{query.purpose},'%')
and t1.create_time >=#{query.beginTime} and create_time <=#{query.endTime}
order by t1.status,t1.update_time desc

mapper.update:
UPDATE enroll_user
set
phone = #{phone} ,status = 1 ,union_id = #{unionId}

    ,name = #{name}


    ,area = #{area}


    ,address=#{address}


    ,age=#{age}


    ,grade=#{grade}


    ,remark=#{remark}


    ,purpose=#{purpose}

WHERE
open_id = #{openId}
