mapper.select:
SELECT t1.id, t1.username, t1.real_name, t1.avatar, t1.gender, t1.email, t1.mobile, t1.org_id, t1.super_admin, t1.status, t1.version, t1.deleted, t1.creator, t1.create_time, t1.updater, t1.update_time, (select t3.name from sys_org t3 where t3.id = t1.org_id) orgName, t2.role_id FROM sys_user t1 LEFT JOIN sys_user_role t2 ON t1.id=t2.user_id WHERE t1.super_admin = 0 AND t1.deleted = 0
and t1.username like concat('%',#{username},'%')
and t1.mobile = #{mobile}
and t1.gender = #{gender}
AND t2.role_id = #{roleId}

select t1.id, t1.username, t1.real_name, t1.avatar, t1.gender, t1.email, t1.mobile, t1.org_id, t1.super_admin, t1.status, t1.address, t1.age, t1.grade, t1.integral, t1.balance, t1.area from sys_user t1 left join sys_user_role t2 on t1.id = t2.user_id where t1.deleted = 0 and t2.deleted = 0 and t2.role_id = #{roleId}
and t1.username = concat('%',#{username},'%')
and t1.mobile = concat('%',#{mobile},'%')
and t1.gender = #{gender}

mapper.update:
update sys_user set
	submit_times=submit_times+1,
	correct_times=correct_times+#{correct}
where id = #{userId}
